import React, {Component} from "react";
import Product from "./Product";
import info from "./info";

class Products extends Component {
  constructor(){
    super()
    this.state={
      info:  info
  
  
    }
  }
  
mapping(x){
 const members = x.map( member => <Product
             key={member.id}
             img={member.img}
             name={member.name}
             ingredients={member.ingredients}
             price={member.price}
     />

);
     return members

}


render(){
return(
    <div className="row">
    {this.mapping(this.state.info)}
    </div>
)
}}
export default Products
