import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useHistory } from 'react-router-dom';

export const Login = (props) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const history = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    login();
  };

 
  const Signin = () => {
    const history = useHistory();
  
    async function Signin() {
      try {
        const response = await fetch(`http://localhost:8080/user/login?email=yosr@gmail.com&password=12345678`, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
          },
        });
  
        if (response.ok) {
          // Email and password are in the database, redirect to home page
          history.push('/about');
        } else {
          // Email and password are not in the database, redirect to registration page
          history.push('/registration');
        }
      } catch (error) {
        // Handle network or other errors
        console.error('Error:', error);
      }
    }}
  
  return (
    <div className="auth-form-container">
      <h2>Login</h2>
      <form className="login-form" onSubmit={handleSubmit}>
        <label htmlFor="email">Email</label>
        <input value={email} onChange={(e) => setEmail(e.target.value)} type="email" placeholder="youremail@gmail.com" id="email" name="email" />
        <label htmlFor="password">Password</label>
        <input value={password} onChange={(e) => setPassword(e.target.value)} type="password" placeholder="********" id="password" name="password" />
        <button onClick={Signin} type="submit">Log In</button>
      </form>
      <button className="link-btn" onClick={() => props.onFormSwitch('register')}>
        Don't have an account? Register here.
      </button>
    </div>
  );
};
