import React, {Component} from "react";
import Product from "./Product";


class Products extends Component {
 

render(){
  const info=[
  
 
    {id:8, img: "/images/Margherita.jpg" , name: 'Pizza MARGHERITA' ,  ingredients:"Tomato sauce and mozzarella from Agerola",  price:"7.5",},
    {id:9, img: "/images/4 formaggi.jpg" , name: 'Pizza 4 formaggi' ,  ingredients:"Tomato sauce and mozzarella from Agerola",  price:"8",},
    {id:7, img: "/images/Marinara.jpg" ,   name: 'Pizza Marinara' ,    ingredients:"Tomato sauce and mozzarella from Agerola",  price:"7",},
    {id:5, img: "/images/Napoli.jpg" ,     name: 'Pizza Napoli' ,      ingredients:"Tomato sauce and mozzarella from Agerola",  price:"9",},
    {id:4, img: "/images/Pescatora.jpg" ,  name: 'Pizza Pescatora' ,   ingredients:"Tomato sauce and mozzarella from Agerola",  price:"10",},
    {id:2, img: "/images/Romana.jpg" ,     name: 'Pizza Romana'  ,     ingredients:"Tomato sauce and mozzarella from Agerola",  price:"8.5",}
  ]

const members =  info.map(member => <Product
            key={member.id}
             img={member.img}
             name={member.name}
             ingredients={member.ingredients}
             price={member.price}
             />

);
return(
    <div className="row">
    {members}
    </div>
)
}}
export default Products
